mapper.select:
select `id` as `order_id`,`actual_price`,`user_id`,`count`,`order_status`,`goods_id`,`pay_price`,`name`,`tel`,`city`,`address`,`create_date`,`delivery_num`,`delivery_name`,`pay_price`,`goods_cost`,`remark`,`province_code`,`city_code`,`district_code`
  from order_details
  where is_deleted = 0

  and app_id = #{appId}


  and order_status = #{orderStatus}


  and order_status in (70,100)


  and id = #{orderId}


  and create_date between #{beginTime} and #{endTime}

 order by create_date desc limit #{page},#{pageSize}

select count(id) from order_details where is_deleted = 0

  and app_id = #{appId}


  and order_status = #{orderStatus}


  and order_status in (70,100)


  and id = #{orderId}


  and create_date between #{beginTime} and #{endTime}

select count(*) from order_details where order_status = #{orderStatus}

  and app_id = #{appId}

  and is_deleted = 0

select `id` as `order_id`,`user_id`,`count`,`order_status`,`pay_plateform`,`goods_id`,`pay_price`,`delivery_num`,`delivery_name`,`pay_price`,`goods_cost`,`remark`,`name`,`tel`,`city`,`address`,`create_date`,`province_code`,`city_code`,`district_code`
from order_details where is_deleted = 0 and order_status = #{orderStatus}
order by create_date desc
limit #{currentPage},#{pageSize}
